import React,{useState, useRef,useEffect} from 'react';

export function AmenitiesInput() {
  const [inputValue, setInputValue] = useState('');
  const [suggestions, setSuggestions] = useState(['Apple', 'Banana', 'Cherry', 'Date']);
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);

    // Filter suggestions based on the input value
    const filteredSuggestions = suggestions.filter((suggestion) =>
      suggestion.toLowerCase().includes(value.toLowerCase())
    );

    setSuggestions(filteredSuggestions);
  };

  const handleSuggestionClick = (suggestion) => {
    // Remove the clicked suggestion from the suggestions list
    const updatedSuggestions = suggestions.filter((s) => s !== suggestion);
    setSuggestions(updatedSuggestions);

    // Add the clicked suggestion to the selected options array
    setSelectedOptions([...selectedOptions, suggestion]);

    // Clear the input field
    setInputValue('');
  };

  return (
    <div>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        placeholder="Type to search"
      />
      <ul>
        {suggestions.map((suggestion, index) => (
          <li key={index} onClick={() => handleSuggestionClick(suggestion)}>
            {suggestion}
          </li>
        ))}
      </ul>
      <div>
        Selected Options:
        <ul>
          {selectedOptions.map((option, index) => (
            <li key={index}>{option}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export function FormInput({name, label, type, onChange }) {
  const [value, setValue] = useState('');

  const handleChange = (e) => {
    setValue(e.target.value);
    onChange(name, value);
  };

  return (
    <div className="form-input-container">
      <label>{label}:</label>
      <input name={name} type={type} value={value} onChange={handleChange} />
    </div>
  );
}

export function RentalForm() {
  const [formData, setFormData] = useState({
    rentalName: '',
    rentalPricing: '',
    rentalCategory: '',
    amenities: [],
  });

  const handleChange = (inputName, value) => {
    setFormData({ ...formData, [inputName]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
        <FormInput name='rentalName' label='rental Name' type='text' onChange={handleChange} />
        <FormInput name='rentalPricing' label='Rental pricing' type='number' onChange={handleChange} />
        <FormInput name='rentalCategory' label='Rental Cartegory' type='text' onChange={handleChange} />
        <AmenitiesInput name='rentalAmenities' onChange={handleChange} />

      <button type="submit">Submit</button>
    </form>
  );
}

export default RentalForm;
