import { useRef, useState, useEffect } from 'react';
import './Style.css';
import './Loading.css';

export const FeedInfo = ({ id }) => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState({
    rules: [],
    amenities: [],
    photos: [],
    // Add more properties for other data if needed
  });

  useEffect(() => {
    // Fetch data using the item's ID
    fetch(`API_ENDPOINT/item/${id}`)
      .then(response => response.json())
      .then(data => {
        setData(data);
        setLoading(false);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, [id]);

  return (
    <div feed={loading && 'loading'} className='feed-information'>
      <div className='feed-info-details'>
        {data.rules.map(rule => (
          <span className='random' key={rule.id}>{rule.text}</span>
        ))}
        {data.amenities.map(amenity => (
          <span className='random' key={amenity.id}>{amenity.name}</span>
        ))}
        {/* Display other data properties as needed */}
      </div>
      <div className='feed-info-map'></div>
    </div>
  );
};
