import React, { useState } from 'react';
import './Style.css'
function ImageUpload() {
  const [images, setImages] = useState([]);
  const [imageProperties, setImageProperties] = useState([]);

  const handleImageUpload = (event) => {
    const selectedImages = Array.from(event.target.files);

    const newImages = selectedImages.map((image) => {
      return URL.createObjectURL(image);
    });

    setImages([...images, ...newImages]);
  };

  const handlePropertyChange = (index, property, value) => {
    const updatedProperties = [...imageProperties];
    updatedProperties[index] = {
      ...updatedProperties[index],
      [property]: value,
    };
    setImageProperties(updatedProperties);
  };

  const handleLogData = () => {
    console.log(images);
    console.log(imageProperties);
  };

  return (
    <div>
      <input
        type="file"
        accept="image/*"
        multiple
        onChange={handleImageUpload}
      />
      <div className="image-preview">
        {images.map((image, index) => (
          <div key={index}>
            <img src={image} alt={`Image ${index}`} />
            <input
              type="text"
              placeholder="Hashtags"
              onChange={(e) => handlePropertyChange(index, 'hashtags', e.target.value)}
            />
            <input
              type="text"
              placeholder="Links"
              onChange={(e) => handlePropertyChange(index, 'links', e.target.value)}
            />
            <input
              type="text"
              placeholder="Tags"
              onChange={(e) => handlePropertyChange(index, 'tags', e.target.value)}
            />
          </div>
        ))}
      </div>
      <button onClick={handleLogData}>Log Data</button>
    </div>
  );
}

export default ImageUpload;
