import React, { useState } from 'react';

const Step3 = ({ onSubmit, onPrev, formData }) => {
  const [amenitiesSuggestions, setAmenitiesSuggestions] = useState([
    'Swimming Pool',
    'Gym',
    'Parking',
    'Laundry',
    // Add more amenities...
  ]);
  const [selectedAmenities, setSelectedAmenities] = useState(formData.information?.amenities || []);

  const [rulesText, setRulesText] = useState(formData.information?.rules?.join('\n') || '');

  const [contactPlatformSuggestions, setContactPlatformSuggestions] = useState([
    'WhatsApp',
    'Instagram',
    'Facebook',
    // Add more contact platforms...
  ]);
  const [selectedContactPlatforms, setSelectedContactPlatforms] = useState([]);

  const [pricing, setPricing] = useState(formData.information?.pricing || {});

  const [agency, setAgency] = useState(formData.information?.details?.agency || '');
  const [showAgencyDetails, setShowAgencyDetails] = useState(formData.information?.details?.agency ? true : false);

  const [rentFine, setRentFine] = useState(formData.information?.pricing?.fineIfRentExceeded || '');

  const [contact, setContact] = useState(formData.information?.details?.contact || {});
  const [contactPlatform, setContactPlatform] = useState('');

  const [autoSuggestions, setAutoSuggestions] = useState(['Instagram', 'WhatsApp', 'Facebook']);

  const handleAmenitiesChange = (event) => {
    const amenity = event.target.value;
    if (amenity && amenitiesSuggestions.includes(amenity)) {
      setSelectedAmenities([...selectedAmenities, amenity]);
      setAmenitiesSuggestions(amenitiesSuggestions.filter(suggestion => suggestion !== amenity));
      event.target.value = '';
    }
  };

  const handleRemoveAmenity = (amenity) => {
    setSelectedAmenities(selectedAmenities.filter(selected => selected !== amenity));
    setAmenitiesSuggestions([...amenitiesSuggestions, amenity]);
  };

  const handleRulesChange = (event) => {
    setRulesText(event.target.value);
  };

  const handleAddContactPlatform = () => {
    if (contactPlatform && contactPlatformSuggestions.includes(contactPlatform)) {
      setSelectedContactPlatforms([...selectedContactPlatforms, contactPlatform]);
      setContactPlatformSuggestions(contactPlatformSuggestions.filter(suggestion => suggestion !== contactPlatform));
      setContactPlatform('');
    }
  };

  const handleContactPlatformChange = (event) => {
    setContactPlatform(event.target.value);
  };

  const handleSelectContactPlatform = (platform) => {
    setSelectedContactPlatforms([...selectedContactPlatforms, platform]);
    setContactPlatform('');
    setContactPlatformSuggestions(contactPlatformSuggestions.filter(suggestion => suggestion !== platform));
  };

  const handleRemoveContactPlatform = (platform) => {
    setSelectedContactPlatforms(selectedContactPlatforms.filter(selected => selected !== platform));
    setContactPlatformSuggestions([...contactPlatformSuggestions, platform]);
  };

  const handleSubmit = () => {
    const parsedRules = rulesText.split('\n').map(rule => rule.trim()).filter(rule => rule.startsWith('#'));
    const updatedInformation = {
      amenities: selectedAmenities,
      rules: parsedRules,
      pricing: {
        ...pricing,
        fineIfRentExceeded: rentFine || undefined
      },
      details: {
        agency,
        contact,
        agencyLink: 'https://www.abcrentals.com' // Hardcoded for now
      }
    };
    onSubmit(updatedInformation);
  };

  return (
    <div className="form-step">
      <h2>Step 3: Information</h2>
      <div className="amenities">
        <h3>Amenities</h3>
        <input type="text" value={contactPlatform} onChange={handleAmenitiesChange} placeholder="Add Amenities" />
        <div className="amenities-list">
          {selectedAmenities.map((amenity, index) => (
            <span key={index}>
              {amenity} <button onClick={() => handleRemoveAmenity(amenity)}>&times;</button>
            </span>
          ))}
        </div>
      </div>
      <div className="rules">
        <h3>Rules</h3>
        <textarea rows="4" value={rulesText} onChange={handleRulesChange}></textarea>
      </div>
      <div className="pricing">
        <h3>Pricing</h3>
        <label>Price:</label>
        <input type="text" value={pricing.price || ''} onChange={(e) => setPricing({ ...pricing, price: e.target.value })} />
        <label>Rent Due Date:</label>
        <input type="text" value={pricing.rentDueDate || ''} onChange={(e) => setPricing({ ...pricing, rentDueDate: e.target.value })} />
        <label>Fine if Rent Exceeded:</label>
        <input type="text" value={rentFine} onChange={(e) => setRentFine(e.target.value)} />
      </div>
      <div className="agency">
        <h3>Agency</h3>
        <label>
          <input type="radio" name="agency" value="ABC Rentals" checked={agency === 'ABC Rentals'} onChange={() => setAgency('ABC Rentals')} />
          ABC Rentals
        </label>
        <label>
          <input type="radio" name="agency" value="Other" checked={agency === 'Other'} onChange={() => setAgency('Other')} />
          Other
        </label>
        {showAgencyDetails && (
          <>
            <label>Agency Name:</label>
            <input type="text" value={agency} onChange={(e) => setAgency(e.target.value)} />
          </>
        )}
      </div>
      <div className="contact">
        <h3>Contact</h3>
        <input type="text" value={contactPlatform} onChange={handleContactPlatformChange} list="suggestions" />
        <datalist id="suggestions">
          {contactPlatformSuggestions.map((suggestion, index) => (
            <option key={index} value={suggestion} />
          ))}
        </datalist>
        <button onClick={handleAddContactPlatform}>Add Contact Platform</button>
        {selectedContactPlatforms.map((platform, index) => (
          <div key={index} className="contact-platform">
            {platform}
            <input
              type="text"
              value={contact[platform] || ''}
              onChange={(e) => handleContactDetailsChange(platform, e.target.value)}
              placeholder={`Contact detail for ${platform}`}
            />
            <button onClick={() => handleRemoveContactPlatform(platform)}>Remove</button>
          </div>
        ))}
      </div>
      <div className="step-buttons">
        <button onClick={onPrev}>Previous</button>
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default Step3;
