import React, { useState } from 'react';

function ImageUpload() {
  const [images, setImages] = useState([]);

  const handleImageUpload = (event) => {
    const selectedImages = Array.from(event.target.files);

    const newImages = selectedImages.map((image) => {
      return {
        file: URL.createObjectURL(image),
        properties: {
          hashtags: [],
          links: [],
          tags: [],
        },
      };
    });

    setImages([...images, ...newImages]);
  };

  const handlePropertyChange = (index, property, value) => {
    const updatedImages = [...images];
    updatedImages[index].properties[property] = value.split(',').map((item) => item.trim());
    setImages(updatedImages);
  };

  const handleLogData = () => {
    console.log(images);
  };

  return (
    <div>
      <input
        type="file"
        accept="image/*"
        multiple
        onChange={handleImageUpload}
      />
      <div className="image-preview">
        {images.map((image, index) => (
          <div key={index}>
            <img src={image.file} alt={`Image ${index}`} />
            <input
              type="text"
              placeholder="Hashtags"
              value={image.properties.hashtags.join(', ')}
              onChange={(e) => handlePropertyChange(index, 'hashtags', e.target.value)}
            />
            <input
              type="text"
              placeholder="Links"
              value={image.properties.links.join(', ')}
              onChange={(e) => handlePropertyChange(index, 'links', e.target.value)}
            />
            <input
              type="text"
              placeholder="Tags"
              value={image.properties.tags.join(', ')}
              onChange={(e) => handlePropertyChange(index, 'tags', e.target.value)}
            />
          </div>
        ))}
      </div>
      <button onClick={handleLogData}>Log Data</button>
    </div>
  );
}

export default ImageUpload;
