import React,{useState} from 'react';

export function RentalNameInput({ onChange }) {
  const handleChange = (e) => {
    onChange('rentalName', e.target.value);
  };

  return (
    <div className="form-input-container">
      <label>Rental Name:</label>
      <input type="text" onChange={handleChange} />
    </div>
  );
}

export function AmenitiesInput() {
  const [inputValue, setInputValue] = useState('');
  const [suggestions, setSuggestions] = useState([
    'Swimming Pool',
    'Gym',
    'Parking',
    'Wi-Fi',
    'Laundry',
    // Add more suggestions as needed
  ]);
  const [selectedAmenities, setSelectedAmenities] = useState([]);
  const [radialValue, setRadialValue] = useState('value1');

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);
    // Filter suggestions based on input value
    const filteredSuggestions = suggestions.filter((suggestion) =>
      suggestion.toLowerCase().includes(value.toLowerCase())
    );
    // Update suggestions state
    setSuggestions(filteredSuggestions);
  };

  const handleSuggestionClick = (suggestion) => {
    // Add the selected suggestion to the list of selected amenities
    setSelectedAmenities([
      ...selectedAmenities,
      { amenity: suggestion, radialValue: radialValue },
    ]);
    setInputValue('');
    // Clear suggestions

  };

  const handleRadialChange = (e) => {
    setRadialValue(e.target.value);
  };

  const handleRemoveAmenity = (index) => {
    const newAmenities = [...selectedAmenities];
    newAmenities.splice(index, 1);
    setSelectedAmenities(newAmenities);
  };

  return (
    <div className="form-input-container">
      <label>Rental amenity:</label>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        placeholder="Start typing amenities..."
      />
      <ul className="suggestions-list">
        {suggestions.map((suggestion, index) => (
          <li key={index} onClick={() => handleSuggestionClick(suggestion)}>
            {suggestion}
          </li>
        ))}
      </ul>
      <div>
        <strong>Selected Amenities:</strong>
        {selectedAmenities.map((amenityObj, index) => (
          <div key={index}>
            {amenityObj.amenity}
            <input
              type="radio"
              name={`radialRadio${index}`}
              value="value1"
              checked={amenityObj.radialValue === 'value1'}
              onChange={handleRadialChange}
            />
            <input
              type="radio"
              name={`radialRadio${index}`}
              value="value2"
              checked={amenityObj.radialValue === 'value2'}
              onChange={handleRadialChange}
            />
            <button onClick={() => handleRemoveAmenity(index)}>Remove</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export function RentalPricingInput() {
  const [pricing, setPricing] = useState('');

  const handleChange = (e) => {
    setPricing(e.target.value);
  };

  return (
    <div className="form-input-container">
      <label>Rental Pricing:</label>
      <input type="text" value={pricing} onChange={handleChange} />
    </div>
  );
}

export function RentalCategoryInput() {
  const [category, setCategory] = useState('');

  const handleChange = (e) => {
    setCategory(e.target.value);
  };

  return (
    <div className="form-input-container">
      <label>Rental Category:</label>
      <input type="text" value={category} onChange={handleChange} />
    </div>
  );
}

export function RentalForm() {
  const [formData, setFormData] = useState({
    rentalName: '',
    rentalPricing: '',
    rentalCategory: '',
    amenities: [],
    // Add state properties for other inputs as needed
  });

  const handleChange = (inputName, value) => {
    setFormData({ ...formData, [inputName]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here, you can access formData and submit it as needed (e.g., send it to an API).
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <RentalNameInput onChange={handleChange} />
      <RentalPricingInput onChange={handleChange} />
      <RentalCategoryInput onChange={handleChange} />
      <AmenitiesInput onChange={handleChange} />
      {/* Include other input components with onChange handlers */}
      <button type="submit">Submit</button>
    </form>
  );
}

export default RentalForm;
