import React,{useState} from 'react';

export function RentalNameInput({ onChange }) {
  const handleChange = (e) => {
    onChange('rentalName', e.target.value);
  };

  return (
    <div className="form-input-container">
      <label>Rental Name:</label>
      <input type="text" onChange={handleChange} />
    </div>
  );
}

export function AmenitiesInput() {
  const [inputValue, setInputValue] = useState('');
  const [selectedAmenities, setSelectedAmenities] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false); // To control when to show suggestions
  const suggestions = ["Apple", "Banana", "Cherry", "Date", "Grape"];

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);
    setShowSuggestions(value.trim() !== ''); // Show suggestions when input is not empty
  };

  const handleSelectAmenity = (suggestion) => {
    setSelectedAmenities([...selectedAmenities, suggestion]);
    setInputValue('');
    setShowSuggestions(false); // Hide suggestions after selection
  };

  const handleDeselectAmenity = (suggestion) => {
    const updatedAmenities = selectedAmenities.filter((amenity) => amenity !== suggestion);
    setSelectedAmenities(updatedAmenities);
  };

  return (
    <div>
      <label htmlFor="autocomplete">Type something:</label>
      <input
        type="text"
        id="autocomplete"
        value={inputValue}
        onFocus={() => setShowSuggestions(true)} // Show suggestions on input focus
        onChange={handleInputChange}
      />
      <button onClick={() => handleSelectAmenity(inputValue)}>Select</button>
      <div>
        <h2>Selected Amenities:</h2>
        {selectedAmenities.map((amenity, index) => (
          <div key={index}>
            <span>{amenity}</span>
            <button onClick={() => handleDeselectAmenity(amenity)}>Deselect</button>
          </div>
        ))}
      </div>
      {showSuggestions && (
        <div>
          <h2>Suggestions:</h2>
          {suggestions
            .filter((suggestion) => suggestion.toLowerCase().includes(inputValue.toLowerCase()))
            .map((suggestion, index) => (
              <div key={index}>
                <span>{suggestion}</span>
                <button onClick={() => handleSelectAmenity(suggestion)}>Select</button>
              </div>
            ))}
        </div>
      )}
    </div>
  );
}



export function RentalPricingInput() {
  const [pricing, setPricing] = useState('');

  const handleChange = (e) => {
    setPricing(e.target.value);
  };

  return (
    <div className="form-input-container">
      <label>Rental Pricing:</label>
      <input type="text" value={pricing} onChange={handleChange} />
    </div>
  );
}

export function RentalCategoryInput() {
  const [category, setCategory] = useState('');

  const handleChange = (e) => {
    setCategory(e.target.value);
  };

  return (
    <div className="form-input-container">
      <label>Rental Category:</label>
      <input type="text" value={category} onChange={handleChange} />
    </div>
  );
}

export function RentalForm() {
  const [formData, setFormData] = useState({
    rentalName: '',
    rentalPricing: '',
    rentalCategory: '',
    amenities: [],
    // Add state properties for other inputs as needed
  });

  const handleChange = (inputName, value) => {
    setFormData({ ...formData, [inputName]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here, you can access formData and submit it as needed (e.g., send it to an API).
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <RentalNameInput onChange={handleChange} />
      <RentalPricingInput onChange={handleChange} />
      <RentalCategoryInput onChange={handleChange} />
      <AmenitiesInput onChange={handleChange} />
      {/* Include other input components with onChange handlers */}
      <button type="submit">Submit</button>
    </form>
  );
}

export default RentalForm;
