function Validate(inputName, type, required, value) {
  const error = { isValid: false, message: '' };
  if(required && value === ''){
    error.message = ' should not be left empty';
    error.isValid = false
    return error;
  }
  if(!required){
    error.isValid = true
  }

  if(value !== ''){
    const emailRegex = /^[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Za-z]{2,}$/;
    const textRegex = /^[A-Za-z\s]+$/;
    const numberRegex = /^\d+$/;
    switch (type) {
    case 'email':
      if (!emailRegex.test(value)) {
        error.isValid = false
        error.message = 'Invalid email format';
      } else {
        error.message = '';
      }
      break;

    case 'text':
      if (!textRegex.test(value)) {
        error.message = 'Invalid text format';
        error.isValid = false
      } else {
        error.message = '';
      }
      break;

    case 'number':
      if (!numberRegex.test(value)) {
        error.message = 'Invalid number format';
        error.isValid = false
      } else {
        error.message = '';
      }
      break;

    default:
      console.error('Invalid validation type');
  }
  }

  return error;
}


