import React, { useState } from 'react';

const Step3 = ({ onSubmit, onPrev, formData }) => {
  const [amenitiesSuggestions, setAmenitiesSuggestions] = useState([
    // List of suggested amenities
    'Swimming Pool',
    'Gym',
    'Parking',
    'Laundry',
    // Add more amenities...
  ]);

  const [selectedAmenities, setSelectedAmenities] = useState([]);

  const [rulesText, setRulesText] = useState('');

  const [contactPlatformSuggestions, setContactPlatformSuggestions] = useState([
    // List of suggested contact platforms
    'WhatsApp',
    'Instagram',
    'Facebook',
    // Add more contact platforms...
  ]);

  const [selectedContactPlatforms, setSelectedContactPlatforms] = useState([]);

  const [rentFine, setRentFine] = useState(false);
  const [rentFineAmount, setRentFineAmount] = useState('');

  const handleAddAmenity = (amenity) => {
    setSelectedAmenities([...selectedAmenities, amenity]);
    setAmenitiesSuggestions(amenitiesSuggestions.filter(suggestion => suggestion !== amenity));
  };

  const handleAddRule = () => {
    if (rulesText === '') {
      setRulesText('# ');
    } else {
      setRulesText(rulesText + '\n# ');
    }
  };

  const handleRuleChange = (event) => {
    setRulesText(event.target.value);
  };

  const handleAddContactPlatform = (platform) => {
    setSelectedContactPlatforms([...selectedContactPlatforms, platform]);
    setContactPlatformSuggestions(contactPlatformSuggestions.filter(suggestion => suggestion !== platform));
  };

  const handleRentFineChange = (event) => {
    setRentFine(event.target.value === 'true');
  };

  return (
    <div className="form-step step3">
      <h2>Additional Information</h2>
      <div className="amenities">
        <h3>Amenities</h3>
        <input
          type="text"
          placeholder="Type amenities..."
          list="amenities-suggestions"
          onChange={(e) => setAmenitiesSuggestions(amenitiesSuggestions.filter(suggestion => !selectedAmenities.includes(suggestion)))}
        />
        <datalist id="amenities-suggestions">
          {amenitiesSuggestions.map((suggestion, index) => (
            <option key={index} value={suggestion} />
          ))}
        </datalist>
        <div className="selected-amenities">
          {selectedAmenities.map((amenity, index) => (
            <span key={index} onClick={() => setSelectedAmenities(selectedAmenities.filter(a => a !== amenity))}>
              {amenity} &times;
            </span>
          ))}
        </div>
        <button onClick={handleAddAmenity}>Add Amenity</button>
      </div>
      <div className="rules">
        <h3>Rules</h3>
        <textarea
          rows="5"
          placeholder="Add rules..."
          value={rulesText}
          onChange={handleRuleChange}
        />
        <button onClick={handleAddRule}>Next Line</button>
      </div>
      <div className="contact">
        <h3>Contact Platforms</h3>
        <input
          type="text"
          placeholder="Type contact platform..."
          list="contact-platform-suggestions"
          onChange={(e) => setContactPlatformSuggestions(contactPlatformSuggestions.filter(suggestion => !selectedContactPlatforms.includes(suggestion)))}
        />
        <datalist id="contact-platform-suggestions">
          {contactPlatformSuggestions.map((suggestion, index) => (
            <option key={index} value={suggestion} />
          ))}
        </datalist>
        <div className="selected-contact-platforms">
          {selectedContactPlatforms.map((platform, index) => (
            <div key={index}>
              <span onClick={() => setSelectedContactPlatforms(selectedContactPlatforms.filter(p => p !== platform))}>
                {platform} &times;
              </span>
              <input type="text" placeholder={`Enter ${platform} details...`} />
            </div>
          ))}
        </div>
      </div>
      <div className="rent-fine">
        <h3>Rent Fine</h3>
        <label>
          <input type="radio" value="true" checked={rentFine} onChange={handleRentFineChange} />
          Yes
        </label>
        <label>
          <input type="radio" value="false" checked={!rentFine} onChange={handleRentFineChange} />
          No
        </label>
        {rentFine && (
          <input
            type="text"
            placeholder="Enter fine amount..."
            value={rentFineAmount}
            onChange={(e) => setRentFineAmount(e.target.value)}
          />
        )}
      </div>
      <div className="step-buttons">
        <button onClick={onPrev}>Previous</button>
        <button onClick={() => onSubmit({ amenities: selectedAmenities, rules: rulesText, rentFine: rentFineAmount })}>Submit</button>
      </div>
    </div>
  );
};

export default Step3;
