import React, { useState, useEffect } from 'react';
import './Style.css';

function ImageUpload() {
  const [imgSrc, setImgSrc] = useState('https://uploader-assets.s3.ap-south-1.amazonaws.com/codepen-default-placeholder.png');
  const [imgList, setImgList] = useState([]);
  const [showDelete, setShowDelete] = useState(false);

  const handleFileChange = (e) => {
    const file = e.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onload = (event) => {
        setImgSrc(event.target.result);

        const newImgList = imgList.map((img) => ({ ...img, isActive: false }));
        newImgList.push({ src: event.target.result, isActive: true });
        setImgList(newImgList);
        setShowDelete(true);
      };

      reader.readAsDataURL(file);
    }
  };

  const handleImageClick = (index) => {
    const newImgList = imgList.map((img, i) => ({ ...img, isActive: i === index }));
    setImgList(newImgList);
    setImgSrc(imgList[index].src);
    setShowDelete(true);
  };

  const handleDeleteImage = () => {
    const newImgList = imgList.filter((img) => !img.isActive);
    setImgList(newImgList);

    if (newImgList.length > 0) {
      setImgSrc(newImgList[0].src);
      setShowDelete(true);
    } else {
      setImgSrc('https://uploader-assets.s3.ap-south-1.amazonaws.com/codepen-default-placeholder.png');
      setShowDelete(false);
    }
  };

  // New function to handle removing the active image
  const handleRemoveImage = () => {
    const newImgList = imgList.filter((img) => !img.isActive);
    setImgList(newImgList);

    if (newImgList.length > 0) {
      setImgSrc(newImgList[0].src);
      setShowDelete(true);
    } else {
      setImgSrc('https://uploader-assets.s3.ap-south-1.amazonaws.com/codepen-default-placeholder.png');
      setShowDelete(false);
    }
  };

  useEffect(() => {
    const handleResize = () => {
      const initWidth = document.querySelector('.img-preview-big').offsetWidth;
      document.querySelector('.img-preview-big').style.height = initWidth + 'px';
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div className="main-wrapper">
      <div className="img-upload-plugin">
        <div className="img-upload-handler">
          <div className="img-preview-big">
            <img src={imgSrc} alt="Preview" />
            {showDelete && (
              <div className="img-delete" onClick={handleDeleteImage}>
                <img src="https://uploader-assets.s3.ap-south-1.amazonaws.com/codepen-delete-icon.png" alt="Delete" />
              </div>
            )}
          </div>
        </div>
        <div className="img-preview-operate">
          <div className="img-holder" style={{ display: 'inline-block', padding: '2px' }}>
            {imgList.map((img, index) => (
              <div key={index} className={`img-preview-small ${img.isActive ? 'img-small-selected' : ''}`}>
                <img src={img.src} alt="Preview" />
                <button className="remove-button" onClick={handleRemoveImage}>
                  Remove
                </button>
              </div>
            ))}
          </div>
          <button className="img-add-more" onClick={() => document.querySelector('input[type="file"]').click()}>
            <img src="https://uploader-assets.s3.ap-south-1.amazonaws.com/codepen-add-more-btn.png" alt="Add" />
          </button>
        </div>
        <input type="file" name="img-upload-input" style={{ display: 'none' }} onChange={handleFileChange} />
      </div>
    </div>
  );
}

export default ImageUpload;
