import React,{useState, useRef,useEffect} from 'react';

export const AutocompleteInput = () => {
  const [inputValue, setInputValue] = useState('');
  const [suggestions, setSuggestions] = useState([
    'Apple',
    'Banana',
    'Cherry',
    'Date',
    'Elderberry',
    'Fig',
  ]);
  const [selectedSuggestions, setSelectedSuggestions] = useState([]);

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);

    if (suggestions.includes(value)) {
      const updatedSuggestions = suggestions.filter((suggestion) => suggestion !== value);
      setSuggestions(updatedSuggestions);

      setSelectedSuggestions([...selectedSuggestions, value]);

      setInputValue('');
    }
  };

  const handleRemoveSelected = (selected) => {
    const updatedSelected = selectedSuggestions.filter((item) => item !== selected);
    setSelectedSuggestions(updatedSelected);

    setSuggestions([...suggestions, selected]);
  };

  return (
    <div>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        list="suggestions"
        placeholder="Type here..."
      />
      <datalist id="suggestions">
        {suggestions.map((suggestion, index) => (
          <option key={index} value={suggestion} />
        ))}
      </datalist>
      <div>
        <p>Selected Suggestions:</p>
        <ul>
          {selectedSuggestions.map((selected, index) => (
            <li key={index}>
              {selected}{' '}
              <button onClick={() => handleRemoveSelected(selected)}>Remove</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export function FormInput({name, label, type, onChange }) {
  const [value, setValue] = useState('');

  const handleChange = (e) => {
    setValue(e.target.value);
    onChange(name, value);
  };

  return (
    <div className="form-input-container">
      <label>{label}:</label>
      <input name={name} type={type} value={value} onChange={handleChange} />
    </div>
  );
}

export function RentalForm() {
  const [formData, setFormData] = useState({
    rentalName: '',
    rentalPricing: '',
    rentalCategory: '',
    amenities: [],
  });

  const handleChange = (inputName, value) => {
    setFormData({ ...formData, [inputName]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
        <FormInput name='rentalName' label='rental Name' type='text' onChange={handleChange} />
        <FormInput name='rentalPricing' label='Rental pricing' type='number' onChange={handleChange} />
        <FormInput name='rentalCategory' label='Rental Cartegory' type='text' onChange={handleChange} />
        <AmenitiesInput name='rentalAmenities' onChange={handleChange} />

      <button type="submit">Submit</button>
    </form>
  );
}

export default RentalForm;
