import React, { useState } from 'react';

const Step2 = ({ onNext, onPrev, formData }) => {
  const [photos, setPhotos] = useState(formData.photos || []);
  const [allowUserUpload, setAllowUserUpload] = useState(formData.allowUserUpload || false);

  const handleNext = () => {
    onNext({ photos, allowUserUpload });
  };

  const handleImageChange = (index, key, value) => {
    if (key === 'image' && photos.length < 10) {
      const updatedPhotos = [...photos];
      updatedPhotos[index][key] = value;
      updatedPhotos[index].links = {};
      updatedPhotos[index].src = {};
      setPhotos(updatedPhotos);
    }
  };

  const handleRemoveImage = (index) => {
    const updatedPhotos = [...photos];
    updatedPhotos.splice(index, 1);
    setPhotos(updatedPhotos);
  };

  return (
    <div className="form-step">
      <h2>Step 2: Upload Photos</h2>
      <div className="photo-inputs">
        {photos.map((photo, index) => (
          <div key={index} className="photo-input">
            {photo.image && (
              <div className="uploaded-image">
                <img src={URL.createObjectURL(photo.image)} alt={`Image ${index + 1}`} />
                <button onClick={() => handleRemoveImage(index)}>Remove</button>
              </div>
            )}
            <input
              type="file"
              onChange={(e) => handleImageChange(index, 'image', e.target.files[0])}
              disabled={photos.length >= 10}
            />
            {photo.image && (
              <>
                <input
                  type="text"
                  placeholder="Image Link 1"
                  value={photo.links.link1 || ''}
                  onChange={(e) => handleImageChange(index, 'link1', e.target.value)}
                />
                <input
                  type="text"
                  placeholder="Image Link 2"
                  value={photo.links.link2 || ''}
                  onChange={(e) => handleImageChange(index, 'link2', e.target.value)}
                />
                <input
                  type="text"
                  placeholder="Image Name"
                  value={photo.src.name || ''}
                  onChange={(e) => handleImageChange(index, 'imageName', e.target.value)}
                />
              </>
            )}
          </div>
        ))}
        {photos.length < 10 && (
          <div className="photo-input">
            <input
              type="file"
              onChange={(e) => handleImageChange(photos.length, 'image', e.target.files[0])}
            />
          </div>
        )}
      </div>
      <label>
        Allow User Upload:
        <input
          type="checkbox"
          checked={allowUserUpload}
          onChange={() => setAllowUserUpload(!allowUserUpload)}
        />
      </label>
      <button onClick={onPrev}>Previous</button>
      <button onClick={handleNext}>Next</button>
    </div>
  );
};

export default Step2;
