import React, { useState } from 'react';

const Step2 = ({ onNext, onPrev, formData }) => {
  const [photos, setPhotos] = useState(formData.photos || []);
  const [hasBooleanValue, setHasBooleanValue] = useState(formData.hasBooleanValue || false);

  const handleNext = () => {
    onNext({ photos, hasBooleanValue });
  };

  const handleImageChange = (index, key, value) => {
    const updatedPhotos = [...photos];
    updatedPhotos[index][key] = value;
    setPhotos(updatedPhotos);
  };

  const handleAddImage = () => {
    if (photos.length < 15) {
      setPhotos([...photos, { image: '', links: {}, src: {} }]);
    }
  };

  const handleRemoveImage = (index) => {
    const updatedPhotos = photos.filter((_, i) => i !== index);
    setPhotos(updatedPhotos);
  };

  const displayUploadedImages = () => {
    return photos.map((photo, index) => (
      <div key={index} className="uploaded-image">
        <img src={URL.createObjectURL(photo.image)} alt={`Image ${index + 1}`} />
        <p>Image Link: {photo.links.link1 || 'N/A'}</p>
        <p>Image Name: {photo.src.name || 'N/A'}</p>
      </div>
    ));
  };

  return (
    <div className="form-step">
      <h2>Step 2: Upload Photos</h2>
      {photos.map((photo, index) => (
        <div key={index} className="photo-input">
          <input
            type="file"
            onChange={(e) => handleImageChange(index, 'image', e.target.files[0])}
          />
          <input
            type="text"
            placeholder="Image Link"
            value={photo.links.link1 || ''}
            onChange={(e) => handleImageChange(index, 'links', { ...photo.links, link1: e.target.value })}
          />
          <input
            type="text"
            placeholder="Image Name"
            value={photo.src.name || ''}
            onChange={(e) => handleImageChange(index, 'src', { ...photo.src, name: e.target.value })}
          />
          <button onClick={() => handleRemoveImage(index)}>Remove</button>
        </div>
      ))}
      <button onClick={handleAddImage} disabled={photos.length >= 15}>Add Image</button>
      <div className="uploaded-images">{displayUploadedImages()}</div>
      <div className="allow-user-upload">
        <label htmlFor="allowUpload">Allow User Upload:</label>
        <input
          type="checkbox"
          id="allowUpload"
          checked={hasBooleanValue}
          onChange={() => setHasBooleanValue(!hasBooleanValue)}
        />
      </div>
      <div className="step-buttons">
        <button onClick={onPrev}>Previous</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default Step2;
