import React, { useState } from 'react';

const Step2 = ({ onNext, onPrev, formData }) => {
  const [photos, setPhotos] = useState(formData.photos || []);
  const [hasBooleanValue, setHasBooleanValue] = useState(formData.hasBooleanValue || false);

  const handleImageChange = (index, key, value) => {
    const updatedPhotos = [...photos];
    if (index >= updatedPhotos.length) {
      updatedPhotos.push({
        image: value,
        links: {
          link1: '',
          link2: '',
        },
        src: {
          id: `photo${index + 1}`,
          name: `Photo ${index + 1}`,
        },
      });
    } else {
      if (key.startsWith('links')) {
        const linkKey = key.split('.')[1];
        updatedPhotos[index].links[linkKey] = value;
      } else if (key.startsWith('src')) {
        const srcKey = key.split('.')[1];
        updatedPhotos[index].src[srcKey] = value;
      } else {
        updatedPhotos[index][key] = value;
      }
    }
    setPhotos(updatedPhotos);
  };

  const handleRemoveImage = (index) => {
    const updatedPhotos = [...photos];
    updatedPhotos.splice(index, 1);
    setPhotos(updatedPhotos);
  };

  const handleNext = () => {
    onNext({ photos, hasBooleanValue });
  };

  return (
    <div className="form-step">
      <h2>Step 2: Upload Photos</h2>
      {photos.map((photo, index) => (
        <div key={index} className="photo-input">
          <input
            type="file"
            accept="image/*"
            onChange={(e) => handleImageChange(index, 'image', e.target.files[0])}
          />
          {photo.image && (
            <>
              <img src={URL.createObjectURL(photo.image)} alt={`Photo ${index + 1}`} />
              <button onClick={() => handleRemoveImage(index)}>Remove</button>
              {index === photos.length - 1 && (
                <>
                  <input
                    type="text"
                    placeholder="Image Link 1"
                    value={photo.links.link1}
                    onChange={(e) => handleImageChange(index, `links.link1`, e.target.value)}
                  />
                  <input
                    type="text"
                    placeholder="Image Link 2"
                    value={photo.links.link2}
                    onChange={(e) => handleImageChange(index, `links.link2`, e.target.value)}
                  />
                  <input
                    type="text"
                    placeholder="Image Name"
                    value={photo.src.name}
                    onChange={(e) => handleImageChange(index, `src.name`, e.target.value)}
                  />
                </>
              )}
            </>
          )}
        </div>
      ))}
      {photos.length < 10 && (
        <div className="add-image-button">
          <input
            type="file"
            accept="image/*"
            onChange={(e) => handleImageChange(photos.length, 'image', e.target.files[0])}
          />
        </div>
      )}
      <div className="allow-user-upload">
        <label htmlFor="allowUpload">Allow User Upload:</label>
        <input
          type="checkbox"
          id="allowUpload"
          checked={hasBooleanValue}
          onChange={() => setHasBooleanValue(!hasBooleanValue)}
        />
      </div>
      <div className="step-buttons">
        <button onClick={onPrev}>Previous</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default Step2;
