import React,{useState, useRef,useEffect} from 'react';

export function AmenitiesInput({ name, suggestionArray = ['apple', 'banana', 'mango'], onChange }) {
  const inputRef = useRef(null);
  const [inputValue, setInputValue] = useState('');
  const [selectedAmenities, setSelectedAmenities] = useState([]);
  const [filteredSuggestion, setFilteredSuggestion] = useState([]);
  const [amenities, setAmenities] = useState([]);
  const [isInputFocused, setInputFocused] = useState(false);

  useEffect(() => {
    onChange(name, amenities);
  }, [amenities, onChange]);

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);

    setFilteredSuggestion(
      suggestionArray.filter((suggestion) =>
        suggestion.toLowerCase().includes(value.toLowerCase())
      )
    );
  };

  const handleSelectAmenity = (suggestion) => {
    setSelectedAmenities([...selectedAmenities, suggestion]);
    setInputValue('');

    setFilteredSuggestion(filteredSuggestion.filter((item) => item !== suggestion));
    inputRef.current.focus();

    setAmenities([...amenities, { name: suggestion, value: false }]);
  };

  const handleDeselectAmenity = (suggestion) => {
    setSelectedAmenities(selectedAmenities.filter((amenity) => amenity !== suggestion));
    setFilteredSuggestion([...filteredSuggestion, suggestion]);

    setAmenities(amenities.filter((amenity) => amenity.name !== suggestion));
  };

  const handleRadioChange = (suggestion, newValue) => {
    setAmenities(
      amenities.map((amenity) =>
        amenity.name === suggestion ? { ...amenity, value: newValue } : amenity
      )
    );
  };

  const handleInputFocus = () => {
    setInputFocused(true);
  };

  const handleInputBlur = () => {
    setInputFocused(false);
  };

  return (
    <div>
      <label htmlFor="autocomplete">Select amenities included</label>
      <input
        ref={inputRef}
        name={name}
        type="text"
        id="autocomplete"
        value={inputValue}
        onChange={handleInputChange}
        onFocus={handleInputFocus}
        onBlur={handleInputBlur}
      />
      {isInputFocused && (
        <div>
          <h2>Selected Amenities:</h2>
          {selectedAmenities.map((amenity, index) => (
            <div key={index}>
              <span>{amenity}</span>
              <button type="button" onClick={() => handleDeselectAmenity(amenity)}>
                Deselect
              </button>
              <div>
                <label>
                  <input
                    type="radio"
                    name={`radio_${amenity}`}
                    value={true}
                    onChange={() => handleRadioChange(amenity, true)}
                    checked={
                      amenities.find((item) => item.name === amenity)?.value === true
                    }
                  />
                  True
                </label>
                <label>
                  <input
                    type="radio"
                    name={`radio_${amenity}`}
                    value={false}
                    onChange={() => handleRadioChange(amenity, false)}
                    checked={
                      amenities.find((item) => item.name === amenity)?.value === false
                    }
                  />
                  False
                </label>
              </div>
            </div>
          ))}
        </div>
      )}
      {isInputFocused && (
        <div>
          <h2>Suggestions:</h2>
          {filteredSuggestion.map((suggestion, index) => (
            <div key={index}>
              <span>{suggestion}</span>
              <button type="button" onClick={() => handleSelectAmenity(suggestion)}>
                Select
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export function FormInput({name, label, type, onChange }) {
  const [value, setValue] = useState('');

  const handleChange = (e) => {
    setValue(e.target.value);
    onChange(name, value);
  };

  return (
    <div className="form-input-container">
      <label>{label}:</label>
      <input name={name} type={type} value={value} onChange={handleChange} />
    </div>
  );
}

export function RentalForm() {
  const [formData, setFormData] = useState({
    rentalName: '',
    rentalPricing: '',
    rentalCategory: '',
    amenities: [],
  });

  const handleChange = (inputName, value) => {
    setFormData({ ...formData, [inputName]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
        <FormInput name='rentalName' label='rental Name' type='text' onChange={handleChange} />
        <FormInput name='rentalPricing' label='Rental pricing' type='number' onChange={handleChange} />
        <FormInput name='rentalCategory' label='Rental Cartegory' type='text' onChange={handleChange} />
        <AmenitiesInput name='rentalAmenities' onChange={handleChange} />

      <button type="submit">Submit</button>
    </form>
  );
}

export default RentalForm;
