import React, { Component } from 'react';

class CreateForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      step: 1,
      formData: {
        name: '',
        location: {},
        photos: [],
        amenities: [],
        contact: {},
        pricing: {},
        agency: false,
        rules: [],
        houseCategory: '',
      },
      amenityInput: '',
    };
  }

  handleNextStep = (data) => {
    const { step, formData } = this.state;
    this.setState({
      formData: { ...formData, ...data },
      step: step + 1,
    });
  };

  handlePrevStep = () => {
    const { step } = this.state;
    this.setState({ step: step - 1 });
  };

  handleAddAmenity = () => {
    const { amenityInput, formData } = this.state;
    if (amenityInput) {
      this.setState({
        formData: { ...formData, amenities: [...formData.amenities, amenityInput] },
        amenityInput: '',
      });
    }
  };

  handleRemoveAmenity = (index) => {
    const { formData } = this.state;
    const updatedAmenities = formData.amenities.filter((_, i) => i !== index);
    this.setState({ formData: { ...formData, amenities: updatedAmenities } });
  };

  handleContactChange = (platform, value) => {
    const { formData } = this.state;
    this.setState({
      formData: { ...formData, contact: { ...formData.contact, [platform]: value } },
    });
  };

  render() {
    const { step, formData, amenityInput } = this.state;

    return (
      <div className="create-form-container">
        {step === 1 && (
          <div className="form-step">
            <h3>Rental Information</h3>
            <input
              type="text"
              value={formData.name}
              onChange={(e) =>
                this.handleContactChange('name', e.target.value)
              }
            />
            {/* Other location input fields */}
            <button onClick={() => this.handleNextStep()}>Next</button>
          </div>
        )}
        {step === 2 && (
          <div className="form-step step2">
            <h2>Upload images of the rental</h2>
            {/* Image upload and display */}
            <button onClick={() => this.handlePrevStep()}>Previous</button>
            <button onClick={() => this.handleNextStep()}>Next</button>
          </div>
        )}
        {step === 3 && (
          <div className="form-step">
            <h2>Step 3: Information</h2>
            <div>
              <h3>Amenities</h3>
              <input
                type="text"
                value={amenityInput}
                onChange={(e) => this.setState({ amenityInput: e.target.value })}
              />
              <button onClick={this.handleAddAmenity}>Add Amenity</button>
              <div>
                {formData.amenities.map((amenity, index) => (
                  <div key={index}>
                    {amenity}{' '}
                    <button onClick={() => this.handleRemoveAmenity(index)}>Remove</button>
                  </div>
                ))}
              </div>
            </div>
            <div>
              {/* Other input fields for pricing, agency, rules, houseCategory */}
            </div>
            <button onClick={() => this.handlePrevStep()}>Previous</button>
            <button onClick={() => this.handleSubmit(data)}>Submit</button>
          </div>
        )}
      </div>
    );
  }
}

export default CreateForm;
