import React, { useState, useEffect } from 'react';
import './Style.css';

function ImageUpload() {
  const [imgSrc, setImgSrc] = useState('https://uploader-assets.s3.ap-south-1.amazonaws.com/codepen-default-placeholder.png');
  const [imgList, setImgList] = useState([]);
  const [showDelete, setShowDelete] = useState(false);
  const [linkText, setLinkText] = useState('');
  const [links, setLinks] = useState([]);
  const [uploadedImages, setUploadedImages] = useState([]);

  const handleFileChange = (e) => {
    const file = e.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onload = (event) => {
        setImgSrc(event.target.result);
        setShowDelete(true);
        setLinks([]); // Clear previous links when uploading a new image.

        // Automatically save the current links for the image being replaced.
        if (links.length > 0) {
          const newImgList = [...imgList, { src: imgSrc, links }];
          setImgList(newImgList);
          setLinks([]); // Clear links for the previous image.
        }
      };

      reader.readAsDataURL(file);
    }
  };

  const handleImageClick = (index) => {
    setImgSrc(imgList[index].src);
    setShowDelete(true);
    setLinks(imgList[index].links || []); // Display associated links for the clicked image.
  };

  const handleDeleteImage = () => {
    const newImgList = imgList.filter((img) => img.src !== imgSrc);
    setImgList(newImgList);
    setLinks([]); // Clear links when an image is deleted.
    if (newImgList.length === 0) {
      setImgSrc('https://uploader-assets.s3.ap-south-1.amazonaws.com/codepen-default-placeholder.png');
      setShowDelete(false);
    }
  };

  const handleLinkTextChange = (e) => {
    setLinkText(e.target.value);
  };

  const handleLinkInputKeyPress = (e) => {
    if (e.key === 'Enter' && linkText) {
      const newLinks = [...links, linkText];
      setLinks(newLinks);
      setLinkText('');
    }
  };

  useEffect(() => {
    // Automatically save the current links for the last image when the component unmounts.
    return () => {
      if (links.length > 0) {
        const newImgList = [...imgList, { src: imgSrc, links }];
        setImgList(newImgList);
        setLinks([]); // Clear links for the previous image.
      }
    };
  }, [links, imgSrc, imgList]);

  const handleLogLength = () => {
    console.log('Total uploaded images:', uploadedImages.length);
  };

  return (
    <div className="main-wrapper">
      <div className="img-upload-plugin">
        <div className="img-upload-handler">
          <div className="img-preview-big">
            <img src={imgSrc} alt="Preview" />
            {showDelete && (
              <div className="img-delete" onClick={handleDeleteImage}>
                <img src="https://uploader-assets.s3.ap-south-1.amazonaws.com/codepen-delete-icon.png" alt="Delete" />
              </div>
            )}
          </div>
        </div>
        <div className="img-preview-operate">
          <div className="img-holder" style={{ display: 'inline-block', padding: '2px' }}>
            {imgList.map((img, index) => (
              <div
                key={index}
                className={`img-preview-small ${img.src === imgSrc ? 'img-small-selected' : ''}`}
                onClick={() => handleImageClick(index)}
              >
                <img src={img.src} alt="Preview" />
              </div>
            ))}
          </div>
          <button className="img-add-more" onClick={() => document.querySelector('input[type="file"]').click()}>
            <img src="https://uploader-assets.s3.ap-south-1.amazonaws.com/codepen-add-more-btn.png" alt="Add" />
          </button>
        </div>
        <input type="file" name="img-upload-input" style={{ display: 'none' }} onChange={handleFileChange} />
      </div>

      <div>
        <textarea
          placeholder="Enter links starting with @ (up to 5)"
          value={linkText}
          onChange={handleLinkTextChange}
          onKeyPress={handleLinkInputKeyPress}
        />
      </div>

      <div>
        {links.map((link, index) => (
          <p key={index}>@{link}</p>
        ))}
      </div>

      <div>
        <button onClick={handleLogLength}>Log Image Count</button>
      </div>
    </div>
  );
}

export default ImageUpload;
