export function FormInput({name, type, value, label, required, onChange, message, validity, disabled}) {
  const [valid, setValidity] = useState(validity);
  const [inputValue, setInputValue] = useState(value);

  const handleInputChange=async (e)=>{
    await setInputValue(e.target.value);
    setValidity(Validate(name, type, required, e.target.value));
  }

  useEffect(()=>{
    if(valid.isValid){
      onChange(name, inputValue);
    }
  },[inputValue])

  useEffect(()=>{
    setValidity(valid)
  },[valid])

  return (
    <div className={`form-input-container ${!valid.isValid && 'invalid'}`}>
    <p>{message}</p>
      <label>{label}: {!valid.isValid && valid.message}</label>
      <input disabled={disabled|| false} name={name} type={type} value={inputValue} data_required={required || false} onChange={(e)=>{handleInputChange(e)}} />
    </div>
  );
}

export function TextareaRulesInput({name, type, value, label, required, onChange, validity}){
  const maxCharacters = 100;
  const [text, setText] = useState('# ');
  const [rules, setRules] = useState([]);
  const [characterCount, setCharacterCount] = useState(maxCharacters);
  const textareaRef = useRef(null);
  const [valid, setValidity] = useState(validity);

  useEffect(()=>{
    if(valid.isValid){
      onChange(name, rules);
    }
  },[rules])

  useEffect(()=>{
    setValidity(validity)
  },[validity])

  const handleTextareaChange = async (e) => {
    const inputValue = e.target.value;
    const remainingCharacters = maxCharacters - inputValue.length;

    if (inputValue.trim() === '') {
      setText('# ');
    } else {
      setText(inputValue);
    }

    const lines = inputValue.split('\n');
    const filteredRules = lines
      .filter((line) => line.trim() !== '' && line.trim().startsWith('#'))
      .map((rule) => rule.trim().substring(1));
    await setRules(filteredRules);
    await setCharacterCount(remainingCharacters);
    setValidity(Validate(name, type, required, e.target.value));
    textareaRef.current.style.height = 'auto';
    textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
  };

  const handleEnterPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      setText(text + '\n# ');
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  };

  return (
    <div className={`form-text-container ${!valid.isValid && 'invalid'}`}>
    <p>{valid.message}</p>
    <label>{label}: {!valid.isValid && valid.message} {characterCount}/{maxCharacters} chr</label>
      <textarea ref={textareaRef} value={text} name={name} onChange={handleTextareaChange} type={type} onKeyDown={handleEnterPress} data_required={required || false} disabled={value !== '' && true || false} placeholder="# Start typing your rules here..." style={{ overflowY: 'hidden' }} maxLength={maxCharacters} />
     {/* <div>
        <p>Derived Rules:</p>
        <ul>
          {rules.map((rule, index) => (
            <li key={index}>{rule}</li>
          ))}
        </ul>
      </div>*/}
    </div>
  );
}

export function FileUpload({name, label, required, onChange, validity }) {
  const [inputValue, setInputValue] = useState(null);
  const [error, setError] = useState(null);
  const [valid, setValid] = useState(validity);

  const handleFileChange = async (e) => {
    const file = e.target.files[0];

    if (!file) {
      setInputValue(null);
      setError('No file selected.');
      return;
    }

    const maxSizeInBytes = 1024 * 1024; // 1 MB
    if (file.size > maxSizeInBytes) {
      setInputValue(null);
      setError('File size exceeds the limit (1 MB).');
      return;
    }

    setInputValue(file);
    setError(null);

    onChange(name, inputValue)
  };

  return (
    <div>
    <label>{label}</label>
      <input type="file" value={inputValue} name={name} accept=".pdf,.doc,.docx" onChange={handleFileChange} />
      {error && <p className="error">{error}</p>}
    </div>
  );
}

export function OptionalInputs ({ name, label, required, options, onChange, validity }){
  const initialInputData = {
    [name]: false,
  };

  options.forEach((option) => {
    initialInputData[option] = '';
  });

  const [InputData, setInputData] = useState(initialInputData);
  const [valid, setValidity] = useState(validity);

  const handleInputChange = useCallback((e) => {
    const { name, value, type, checked } = e.target;
    setInputData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  }, []);

  const handleNestedInputs=(name, value)=>{
    setInputData((prevData) => ({...prevData, [name]:value}))
  }

  useEffect(() => {
    onChange(name, InputData, validity)
  }, [InputData, onChange]);

  return (
    <div>
      <label>
        <input type="checkbox" name={name} onChange={handleInputChange} checked={InputData[name]} />
        {label}
      </label>

      {options.map((option) => (
        <div key={option}>
        <FormInput name={option} label={option} type={option=== 'amount' ? 'number':'text'}value={InputData[option]} onChange={handleNestedInputs} disabled={!InputData[name]} validity = { {isValid: false, message:''}}/>
        </div>
      ))}
    </div>
  );
}

