import React, { useState } from 'react';

const Step2 = ({ onNext, onPrev, formData }) => {
  const [images, setImages] = useState([]);
  const [hasBooleanValue, setHasBooleanValue] = useState(formData.hasBooleanValue || false);

  const handleImageChange = (e) => {
    const image = e.target.files[0];
    if (image) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImages([...images, { image, link: '', name: '', url: e.target.result }]);
      };
      reader.readAsDataURL(image);
    }
  };

  const handleLinkChange = (index, value) => {
    const updatedImages = [...images];
    updatedImages[index].link = value;
    setImages(updatedImages);
  };

  const handleNameChange = (index, value) => {
    const updatedImages = [...images];
    updatedImages[index].name = value;
    setImages(updatedImages);
  };

  const handleRemoveImage = (index) => {
    const updatedImages = [...images];
    updatedImages.splice(index, 1);
    setImages(updatedImages);
  };

  const handleNext = () => {
    onNext({ images, hasBooleanValue });
  };

  return (
    <div className="form-step">
      <h2>Step 2: Upload Photos</h2>
      {images.map((imageData, index) => (
        <div key={index} className="image-preview">
          <img src={imageData.url} alt={`Image ${index + 1}`} />
          <button onClick={() => handleRemoveImage(index)}>Remove</button>
          <input
            type="text"
            placeholder="Image Link"
            value={imageData.link}
            onChange={(e) => handleLinkChange(index, e.target.value)}
          />
          <input
            type="text"
            placeholder="Image Name"
            value={imageData.name}
            onChange={(e) => handleNameChange(index, e.target.value)}
          />
        </div>
      ))}
      {images.length < 15 && (
        <div className="add-image-button">
          <input type="file" accept="image/*" onChange={handleImageChange} />
        </div>
      )}
      {images.length >= 15 && <span>Maximum limit of 15 images reached.</span>}
      <div className="allow-user-upload">
        <label htmlFor="allowUpload">Allow User Upload:</label>
        <input
          type="checkbox"
          id="allowUpload"
          checked={hasBooleanValue}
          onChange={() => setHasBooleanValue(!hasBooleanValue)}
        />
      </div>
      <div className="step-buttons">
        <button onClick={onPrev}>Previous</button>
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default Step2;
