import React, { useState, useEffect } from 'react';
import './Style.css';
import './Loading.css';
import { Modal } from '../../Modal/Modal';

const Review = ({ userRating, reviewMessage, date }) => {
  return (
    <div>
      <h3>User Rating: {userRating}</h3>
      <p>{reviewMessage}</p>
      <p>Date: {date}</p>
    </div>
  );
};

export const FeedReviews = ({ toggleModalWithData, id }) => {
  const [reviews, setReviews] = useState([]);
  const [modalData, setModalData] = useState(null);

const API_URL = "http://localhost:5000"; 
  useEffect(() => {
    fetch(`${API_URL}/api/reviews/`)
      .then(response => response.json())
      .then(data => {
        setReviews(data[0].userReviews.reviews); 
      })
      .catch(error => console.error('Error fetching reviews:', error));
  }, [id]);

  const handleToggle = (reviewData) => {
    toggleModalWithData(
      <Review
        userRating={reviewData.userRating}
        reviewMessage={reviewData.reviewMessage}
        date={reviewData.date}
      />
    );
  };

  return (
    <div>
      {reviews.map((review, index) => (
        <div key={index}>
          <Review
            userRating={review.userRating}
            reviewMessage={review.reviewMessage}
            date={review.date}
          />
          <br />
          <br />
        </div>
      ))}
      <div className="display-more-button">
        <span onClick={() => handleToggle(reviews[0])}>
          <b>Show more reviews</b>
        </span>
      </div>
    </div>
  );
};
