import React, { useState } from 'react';

const Step1 = ({ onNext, formData }) => {
  const [name, setName] = useState(formData.name || '');
  const [latitude, setLatitude] = useState(formData.location.latitude || '');
  const [longitude, setLongitude] = useState(formData.location.longitude || '');
  const [residence, setResidence] = useState(formData.location.residence || '');

  const handleNext = () => {
    const location = { latitude, longitude, residence };
    onNext({ name, location });
  };

  return (
    <div className="form-step">
      <h3>Step 1: Basic Information</h3>
      <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
      <input type="text" placeholder="Latitude" value={latitude} onChange={(e) => setLatitude(e.target.value)} />
      <input type="text" placeholder="Longitude" value={longitude} onChange={(e) => setLongitude(e.target.value)} />
      <input type="text" placeholder="Residence" value={residence} onChange={(e) => setResidence(e.target.value)} />
      <button onClick={handleNext}>Next</button>
    </div>
  );
};

export default Step1;
