import React, { useState, useEffect } from 'react';
import './Style.css';
import './Loading.css';
import { Modal } from '../../Modal/Modal';

const Review = ({ review }) => {
  // ... (same as before)
};

export const FeedReviews = ({ toggleModal, toggleModalWithData, id }) => {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    // Fetch reviews data from API using the provided id
    fetch(`API_ENDPOINT/reviews/${id}`)
      .then(response => response.json())
      .then(data => {
        setReviews(data);
      })
      .catch(error => console.error('Error fetching reviews:', error));
  }, [id]);

  const handleToggle = (reviewData) => {
    toggleModalWithData(reviewData);
  };

  return (
    <div>
      {reviews.map((review, index) => (
        <div key={index}>
          <Review review={review} />
          <br/>
          <br/>
        </div>
      ))}
      <div className="display-more-button"><span onClick={() => handleToggle(reviews[0])}><b>Show more reviews</b></span></div>
    </div>
  );
};
