import React, { useState } from 'react';

const Step3 = ({ onSubmit, onPrev, formData }) => {
  const [information, setInformation] = useState(formData.information || {});
  const [amenities, setAmenities] = useState(formData.information?.amenities || {});
  const [rulesText, setRulesText] = useState(formData.information?.rules?.join('\n') || '');
  const [pricing, setPricing] = useState(formData.information?.pricing || {});
  const [agency, setAgency] = useState(formData.information?.details?.agency || '');
  const [showAgencyDetails, setShowAgencyDetails] = useState(formData.information?.details?.agency ? true : false);
  const [contact, setContact] = useState(formData.information?.details?.contact || {});
  const [contactPlatform, setContactPlatform] = useState('');
  const [contactDetails, setContactDetails] = useState('');
  const [selectedContactPlatforms, setSelectedContactPlatforms] = useState([]);
  const [autoSuggestions, setAutoSuggestions] = useState(['Instagram', 'WhatsApp', 'Facebook']);

  const handleAmenitiesChange = (key) => {
    setAmenities({ ...amenities, [key]: !amenities[key] });
  };

  const handleRulesChange = (event) => {
    setRulesText(event.target.value);
  };

  const parseRules = () => {
    const parsedRules = rulesText.split('\n').map(rule => rule.trim()).filter(rule => rule.startsWith('#'));
    return parsedRules;
  };

  const handlePricingChange = (key, value) => {
    setPricing({ ...pricing, [key]: value });
  };

  const handleAgencyChange = (value) => {
    setAgency(value);
    setShowAgencyDetails(value === 'ABC Rentals');
  };

  const handleContactPlatformChange = (value) => {
    setContactPlatform(value);
  };

  const handleAddContactPlatform = () => {
    if (contactPlatform && !selectedContactPlatforms.includes(contactPlatform)) {
      setSelectedContactPlatforms([...selectedContactPlatforms, contactPlatform]);
      setAutoSuggestions(autoSuggestions.filter(suggestion => suggestion !== contactPlatform));
      setContactPlatform('');
    }
  };

  const handleContactDetailsChange = (platform, value) => {
    setContact({ ...contact, [platform]: value });
  };

  const handleSubmit = () => {
    const parsedRules = parseRules();
    const updatedInformation = {
      ...information,
      amenities,
      rules: parsedRules,
      pricing,
      details: {
        agency,
        contact,
        agencyLink: 'https://www.abcrentals.com', // Hardcoded for now
      },
    };
    onSubmit(updatedInformation);
  };

  return (
    <div className="form-step">
      <h2>Step 3: Information</h2>
      <div className="amenities">
        <h3>Amenities</h3>
        <label>
          <input type="checkbox" checked={amenities.wifi} onChange={() => handleAmenitiesChange('wifi')} />
          Wifi
        </label>
        <label>
          <input type="checkbox" checked={amenities.water} onChange={() => handleAmenitiesChange('water')} />
          Water
        </label>
        <label>
          <input type="checkbox" checked={amenities.security} onChange={() => handleAmenitiesChange('security')} />
          Security
        </label>
      </div>
      <div className="rules">
        <h3>Rules</h3>
        <textarea rows="4" value={rulesText} onChange={handleRulesChange}></textarea>
      </div>
      <div className="pricing">
        <h3>Pricing</h3>
        <label>Price:</label>
        <input type="text" value={pricing.price || ''} onChange={(e) => handlePricingChange('price', e.target.value)} />
        <label>Rent Due Date:</label>
        <input type="text" value={pricing.rentDueDate || ''} onChange={(e) => handlePricingChange('rentDueDate', e.target.value)} />
        <label>Fine if Rent Exceeded:</label>
        <input type="text" value={pricing.fineIfRentExceeded || ''} onChange={(e) => handlePricingChange('fineIfRentExceeded', e.target.value)} />
      </div>
      <div className="agency">
        <h3>Agency</h3>
        <label>
          <input type="radio" name="agency" value="ABC Rentals" checked={agency === 'ABC Rentals'} onChange={() => handleAgencyChange('ABC Rentals')} />
          ABC Rentals
        </label>
        <label>
          <input type="radio" name="agency" value="Other" checked={agency === 'Other'} onChange={() => handleAgencyChange('Other')} />
          Other
        </label>
        {showAgencyDetails && (
          <>
            <label>Agency Name:</label>
            <input type="text" value={agency} onChange={(e) => setAgency(e.target.value)} />
          </>
        )}
      </div>
      <div className="contact">
        <h3>Contact</h3>
        <label>Contact Platforms:</label>
        <input type="text" value={contactPlatform} onChange={(e) => handleContactPlatformChange(e.target.value)} list="suggestions" />
        <datalist id="suggestions">
          {autoSuggestions.map((suggestion, index) => (
            <option key={index} value={suggestion} />
          ))}
        </datalist>
        <button onClick={handleAddContactPlatform}>Add Contact Platform</button>
        {selectedContactPlatforms.map((platform, index) => (
          <div key={index} className="contact-platform">
            {platform}
            <input
              type="text"
              value={contact[platform] || ''}
              onChange={(e) => handleContactDetailsChange(platform, e.target.value)}
              placeholder={`Contact detail for ${platform}`}
            />
          </div>
        ))}
      </div>
      <div className="step-buttons">
        <button onClick={onPrev}>Previous</button>
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default Step3;
