import React, { useState } from 'react';
import { Step1, Step2, Step3 } from './FormSteps';
import './Form.css';

const CreateForm = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    name: '',
    location: {},
    information: {},
    photos: [],
  });

  const handleNextStep = (data) => {
    setFormData({ ...formData, ...data });
    setStep(step + 1);
  };

  const handlePrevStep = () => {
    setStep(step - 1);
  };

  const handleSubmit = async () => {
    try {
      // Perform API call here using formData
      console.log('Data submitted successfully');
    } catch (error) {
      console.error('Error submitting data:', error);
    }
  };

  return (
    <div className="create-form-container">
      {step === 1 && <Step1 onNext={handleNextStep} formData={formData} />}
      {step === 2 && <Step2 onNext={handleNextStep} onPrev={handlePrevStep} formData={formData} />}
      {step === 3 && <Step3 onSubmit={handleSubmit} onPrev={handlePrevStep} formData={formData} />}
    </div>
  );
};

export default CreateForm;
