import React,{useState, useRef,useEffect} from 'react';

export function RentalNameInput({ onChange }) {
  const [value, setValue] = useState('');

  const handleChange = (e) => {
    setValue(e.target.value)
    onChange('rentalName', value);
  };

  return (
    <div className="form-input-container">
      <label>Rental Name:</label>
      <input type="text" onChange={handleChange} />
    </div>
  );
}

export function AmenitiesInput({ type = 'Type and select among the suggestions', suggestionArray = ['apple', 'banana', 'mango'], onChange }) {
  const inputRef = useRef(null);
  const [suggestions, changeSuggestionSrcArray] = useState(suggestionArray);
  const [inputValue, setInputValue] = useState('');
  const [selectedAmenities, setSelectedAmenities] = useState([]);
  const [filteredSuggestion, setFilteredSuggestion] = useState(suggestionArray);
  const [amenities, setAmenities] = useState([]); // To store selected options with their states

  // Function to trigger onChange when amenities change
  const handleAmenitiesChange = (updatedAmenities) => {
    setAmenities(updatedAmenities);
    onChange(updatedAmenities);
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInputValue(value);

    // Filter and update suggestions based on user input
    setFilteredSuggestion(suggestions.filter(suggestion => suggestion.toLowerCase().includes(value.toLowerCase())));
  };

  const handleSelectAmenity = (suggestion) => {
    setSelectedAmenities([...selectedAmenities, suggestion]);
    setInputValue('');

    // Remove the selected item from suggestions
    setFilteredSuggestion(suggestions.filter(item => item !== suggestion));
    inputRef.current.focus();
    changeSuggestionSrcArray(suggestions.filter(item => item !== suggestion));

    // Initialize the state for the newly selected option
    const updatedAmenities = [...amenities, { name: suggestion, value: false }];
    handleAmenitiesChange(updatedAmenities);
  };

  const handleDeselectAmenity = (suggestion) => {
    const updatedAmenities = selectedAmenities.filter((amenity) => amenity !== suggestion);
    setSelectedAmenities(updatedAmenities);
    setFilteredSuggestion([...suggestions, suggestion]);
    changeSuggestionSrcArray([...suggestions, suggestion]);

    // Remove the deselected option and its state from amenities
    const updatedAmenitiesList = amenities.filter((amenity) => amenity.name !== suggestion);
    handleAmenitiesChange(updatedAmenitiesList);
  };

  const handleRadioChange = (suggestion, newValue) => {
    // Update the state of the selected option in amenities
    const updatedAmenities = amenities.map((amenity) =>
      amenity.name === suggestion ? { ...amenity, value: newValue } : amenity
    );
    handleAmenitiesChange(updatedAmenities);
  };

  // useEffect to trigger onChange when amenities change
  useEffect(() => {
    onChange(amenities);
  }, [amenities, onChange]);

  return (
    <div>
      <label htmlFor="autocomplete">{type}</label>
      <input
        ref={inputRef}
        type="text"
        id="autocomplete"
        value={inputValue}
        onChange={handleInputChange}
      />
      <button onClick={() => handleSelectAmenity(inputValue)}>Select</button>
      <div>
        <h2>Selected Amenities:</h2>
        {selectedAmenities.map((amenity, index) => (
          <div key={index}>
            <span>{amenity}</span>
            <button onClick={() => handleDeselectAmenity(amenity)}>Deselect</button>
            <div>
              <label>
                <input
                  type="radio"
                  name={`radio_${amenity}`}
                  value={true}
                  onChange={() => handleRadioChange(amenity, true)}
                  checked={amenities.find((item) => item.name === amenity)?.value === true}
                />
                True
              </label>
              <label>
                <input
                  type="radio"
                  name={`radio_${amenity}`}
                  value={false}
                  onChange={() => handleRadioChange(amenity, false)}
                  checked={amenities.find((item) => item.name === amenity)?.value === false}
                />
                False
              </label>
            </div>
          </div>
        ))}
      </div>
      <div>
        <h2>Suggestions:</h2>
        {filteredSuggestion.map((suggestion, index) => (
          <div key={index}>
            <span>{suggestion}</span>
            <button onClick={() => handleSelectAmenity(suggestion)}>Select</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export function RentalPricingInput({ onChange }) {
  const [value, setValue] = useState('');

  const handleChange = (e) => {
    setValue(e.target.value);
    onChange('rentalName', value);
  };

  return (
    <div className="form-input-container">
      <label>Rental Pricing:</label>
      <input type="text" value={pricing} onChange={handleChange} />
    </div>
  );
}

export function RentalCategoryInput({ onChange }) {
  const [value, setValue] = useState('');

  const handleChange = (e) => {
    setValue(e.target.value);
    onChange('rentalCategory', value);
  };

  return (
    <div className="form-input-container">
      <label>Rental Category:</label>
      <input type="text" value={category} onChange={handleChange} />
    </div>
  );
}

export function RentalForm() {
  const [formData, setFormData] = useState({
    rentalName: '',
    rentalPricing: '',
    rentalCategory: '',
    amenities: [],
    // Add state properties for other inputs as needed
  });

  const handleChange = (inputName, value) => {
    setFormData({ ...formData, [inputName]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here, you can access formData and submit it as needed (e.g., send it to an API).
    document.write(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <RentalNameInput onChange={handleChange} />
      <RentalPricingInput onChange={handleChange} />
      <RentalCategoryInput onChange={handleChange} />
      <AmenitiesInput onChange={handleChange} />
      {/* Include other input components with onChange handlers */}
      <button type="submit">Submit</button>
    </form>
  );
}

export default RentalForm;
