import React, { Component } from 'react';

class CreateForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      step: 1,
      formData: {
        name: '',
        location: {},
        photos: [],
        amenities: [],
        contact: {},
        pricing: {},
        agency: false,
        rules: [],
        houseCategory: '',
      },
      amenityInput: '',
      currentImage: { Image: '', links: {} },
      images: [],
      amenitiesSuggestions: [
        'wifi', 'pets', 'water', 'pool', 'socials', // Add more amenities...
      ],
      selectedAmenities: [],
      contactPlatformSuggestions: [
        'WhatsApp', 'Instagram', 'Facebook', // Add more contact platforms...
      ],
      selectedContacts: {},
      contactPlatformDetail: '',
      pricing: {
        price: '',
        rentDueDate: '',
        fineIfRentExceeded: '',
      },
      rentFine: false,
      agency: false,
      agencyDetails: '',
      rulesText: '',
      decodedRules: [],
      houseCategorySuggestions: [
        'Single Room', 'Bedsitter', 'Two Rooms', 'One Bedroom', // Add more house categories...
      ],
      houseCategory: '',
    };
  }

  handleNextStep = (data) => {
    const { step, formData } = this.state;
    this.setState({
      formData: { ...formData, ...data },
      step: step + 1,
    });
  };

  handlePrevStep = () => {
    const { step } = this.state;
    this.setState({ step: step - 1 });
  };

  handleImageChange = (event) => {
    const image = event.target.files[0];
    this.setState({
      currentImage: {
        ...this.state.currentImage,
        Image: image,
        Date: new Date().toISOString(),
        srcUser: `User ${this.state.images.length + 1}`,
      },
    });
  };

  // Other handler methods

  render() {
  const { step, formData, currentImage, images, amenitiesSuggestions, selectedAmenities, contactPlatformSuggestions, selectedContacts, pricing, rentFine, agency, agencyDetails, rulesText, houseCategory, houseCategorySuggestions } = this.state;

  return (
    <div className="create-form-container">
      {step === 1 && (
        <div className="form-step">
          <h3>Rental Information</h3>
          <input type="text" placeholder="Name" value={formData.name} onChange={(e) => this.handleInputChange('name', e.target.value)} />
          <input type="text" placeholder="Latitude" value={formData.location.latitude} onChange={(e) => this.handleLocationChange('latitude', e.target.value)} />
          <input type="text" placeholder="Longitude" value={formData.location.longitude} onChange={(e) => this.handleLocationChange('longitude', e.target.value)} />
          <input type="text" placeholder="Residence" value={formData.location.residence} onChange={(e) => this.handleLocationChange('residence', e.target.value)} />
          <button onClick={this.handleNextStep}>Next</button>
        </div>
      )}
      {step === 2 && (
        <div className="form-step step2">
          <h2>Upload images of the rental</h2>
          <div className="image-upload">
            <input hidden id='image-upload' type="file" accept="image/*" onChange={this.handleImageChange} />
            <label className='input-label' htmlFor='image-upload'>Click to upload an image</label>
            <button className='upload-button' onClick={this.handleUploadImage}>Upload Image</button>
          </div>
          {currentImage.Image && (
            <div className="image-data">
              <img src={URL.createObjectURL(currentImage.Image)} alt="Uploaded" />
              <div className="link-inputs">
                {Object.entries(currentImage.links).map(([key, value], index) => (
                  <div key={key}>
                    <input type="text" placeholder={`Link ${index + 1}`} value={value} onChange={(e) => this.handleLinkChange(key, e.target.value)} />
                    <button onClick={() => this.handleRemoveLink(key)}>Remove</button>
                  </div>
                ))}
                <button className='image-link' onClick={this.handleAddLink}>Add Link</button>
              </div>
            </div>
          )}
          <div className="uploaded-images">
            {images.length > 0 && (
              <>
                <h3>Uploaded Images: {images.length}</h3>
                {images.map((image, index) => (
                  <div key={index} className="uploaded-image">
                    <img src={URL.createObjectURL(image.Image)} alt={`Image ${index + 1}`} />
                    {Object.entries(image.links).map(([key, value], linkIndex) => (
                      <p key={key}>Link {linkIndex + 1}: {value}</p>
                    ))}
                    <p>Date: {image.Date}</p>
                    <p>srcUser: {image.srcUser}</p>
                    <button onClick={() => this.handleRemoveImage(index)}>Remove</button>
                  </div>
                ))}
              </>
            )}
          </div>
          <div className="step-buttons">
            <button onClick={this.handlePrevStep}>Previous</button>
            <button onClick={this.handleNextStep}>Next</button>
          </div>
        </div>
      )}
{step === 3 && (
  <div className="form-step">
    <h2>Step 3: Information</h2>
    <div className="amenities">
      <h3>Amenities</h3>
      <input type="text" onChange={this.handleAmenitiesChange} placeholder="Add Amenities" list="amenities-suggestions" />
      <datalist id="amenities-suggestions">
        {amenitiesSuggestions.map((amenity, index) => (
          <option key={index} value={amenity} />
        ))}
      </datalist>
      <div className="amenities-list">
        {selectedAmenities.map((amenity, index) => (
          <span key={index}>
            {amenity} <button onClick={() => this.handleRemoveAmenity(amenity)}>Remove</button>
          </span>
        ))}
      </div>
    </div>
    <div className="contact">
      <h3>Contact</h3>
      <input type="text" onChange={this.selectContactPlatform} list="contact-suggestions" placeholder="Select Contact Platform" />
      <datalist id="contact-suggestions">
        {contactPlatformSuggestions.map((platform, index) => (
          <option key={index} value={platform} />
        ))}
      </datalist>
      <div className="selected-contacts">
        {Object.keys(selectedContacts).map((platform, index) => (
          <div key={index} className="contact-platform">
            {platform}
            <button onClick={() => this.handleRemoveContactPlatform(platform)}>Remove</button>
            <input
              type="text"
              value={selectedContacts[platform]}
              onChange={(e) => this.handleContactPlatformDetailChange(platform, e.target.value)}
              placeholder={`Enter ${platform} details`}
            />
          </div>
        ))}
      </div>
    </div>
    <div className="pricing">
      <h3>Pricing</h3>
      <div>
        <label>Price:</label>
        <input type="number" value={pricing.price} onChange={(e) => this.handlePricingChange('price', e.target.value)} />
      </div>
      <div>
        <label>Rent Due Date:</label>
        <input type="text" value={pricing.rentDueDate} onChange={(e) => this.handlePricingChange('rentDueDate', e.target.value)} />
      </div>
      <div>
        <label>
          <input type="checkbox" checked={rentFine} onChange={this.handleRentFineChange} /> Rent Fine
        </label>
      </div>
      {rentFine && (
        <div>
          <label>Fine if Rent Exceeded:</label>
          <input type="number" value={pricing.fineIfRentExceeded} onChange={(e) => this.handlePricingChange('fineIfRentExceeded', e.target.value)} />
        </div>
      )}
    </div>
    <div className="agency">
      <h3>Agency</h3>
      <div>
        <label>
          <input type="checkbox" checked={agency} onChange={this.handleAgencyChange} /> ABC Rentals
        </label>
      </div>
      {agency && (
        <div>
          <input type="text" value={agencyDetails} onChange={(e) => this.handleInputChange('agencyDetails', e.target.value)} placeholder="Agency Details" />
        </div>
      )}
    </div>
    <div className="rules">
      <h3>Rules</h3>
      <div>
        <label>Rules:</label>
        <textarea rows="4" value={rulesText} onChange={(e) => this.handleInputChange('rulesText', e.target.value)}></textarea>
      </div>
    </div>
    <div className="house-category">
      <h3>House Category</h3>
      <div>
        <label>House Category:</label>
        <input type="text" value={houseCategory} onChange={(e) => this.handleInputChange('houseCategory', e.target.value)} list="house-suggestions" />
        <datalist id="house-suggestions">
          {houseCategorySuggestions.map((suggestion, index) => (
            <option key={index} value={suggestion} />
          ))}
        </datalist>
      </div>
    </div>
    <div className="step-buttons">
      <button onClick={this.handlePrevStep}>Previous</button>
      <button onClick={this.handleSubmit}>Submit</button>
    </div>
  </div>
)}

    </div>
  );
}

}

export default CreateForm;
